var build = require('tribe/build');

build.activities.register('panes', function (options) {
    return {
        prepare: function (context) {
            build.blocks('addPathsToContext')('js', options.path)(context);
            build.blocks('addFilesToContext')('html', options.path)(context);
            build.blocks('addFilesToContext')('css', options.path)(context);
        }
    };
});