var build = require('tribe/build');

build.activities.register('panes', function (options) {
    return {
        prepare: function (context) {
            build.blocks('addPathsToContext')('js', options.path, /\.js$/)(context);
            build.blocks('addFilesToContext')('html', options.path, /\.html?$/)(context);
            build.blocks('addFilesToContext')('css', options.path, /\.css$/)(context);
        }
    };
});