var utils = require('tribe/utilities'),
    path = require('path'),
    fs = require('q-io/fs'),
    _ = require('underscore');

module.exports = function (property) {
    var combine = {
        to: function (targetPath) {
            return function (context) {
                var result = _.pluck(context[property], 'content').join('\n');
                if (targetPath)
                    return fs.write(utils.paths.resolveAppPath(targetPath), result);
                else
                    return result;
            };
        },
        toBuild: function (filename) {
            return function (context, options) {
                return combine.to(path.join(options.buildPath, filename))(context);
            };
        }
    };
    return combine;
};