var utils = require('tribe/utilities/resources'),
    fs = require('q-io/fs'),
    _ = require('underscore');

module.exports = function (property) {
    return {
        to: function (targetPath) {
            return function (context) {
                var result = _.pluck(context[property], 'content').join('\n');
                if (targetPath)
                    return fs.write(utils.resolveAppPath(targetPath), result);
                else
                    return result;
            };
        }
    };
};