var utils = require('tribe/utilities'),
    options = require('tribe/options'),
    templates = require('tribe/build').templates,
    path = require('path'),
    fs = require('q-io/fs'),
    _ = require('underscore');

module.exports = function (name) {
    return {
        to: function (targetPath) {
            return function (context) {
                var templateData = {
                    utils: utils,
                    context: context,
                    options: options
                };
                var content = _.template(templates(name), templateData);

                if (targetPath)
                    return fs.write(utils.resolveAppPath(targetPath), content);
                else
                    return content;
            };
        }
    };
};