var repository = require('tribe/build/steps'),
    Q = require('q'),
    _ = require('underscore');

var build = module.exports = {
    configure: function (steps, phases) {
        build.steps = _.map(steps, repository.create);
        build.phases = phases;
    },

    execute: function (context) {
        context = context || {};

        return _.reduce(build.phases, function (lastPhase, phase) {
            return Q.when(lastPhase).then(function () {
                return _.reduce(build.steps, function (lastStep, step) {
                    return Q.when(lastStep).then(function () {
                        return step[phase] && step[phase](context);
                    });
                }, null);
            });
        }, null);
    },

    steps: [],
    phases: []
};
