var resources = require('tribe/utilities/resources'),
    path = require('path'),
    factories = {};

module.exports = {
    register: function (name, factory) {
        factories[name] = factory;
    },
    create: function (spec) {
        return factories[spec.name](spec.options);
    },
    loadBuiltin: function () {
        return resources.requireDirectory(path.resolve(__dirname, 'steps'));
    }
};