module.exports = {
    start: function (options) {
        options = require('tribe/options').apply(options);

        require('tribe/build')
            .configure(options.build)
            .execute();

        if (options.debug)
            require('tribe/process/inspector').start(options.inspectorPort);

        require('tribe/test').initialise(true);
        require('tribe/server/http').start();
        require('tribe/server/socket').start();
    }
};
