var express = require('express'),
    app = express(),
    server = require('http').createServer(app),
    fs = require('q-io/fs'),
    log = require('tribe/logger'),
    store = require('tribe/store/fs'),
    options = require('tribe/options');

module.exports = {
    start: function () {
        // these are configuration driven that should be provided by the build engine
        app.get('/', function (req, res) {
            fs.read(options.basePath + 'Build/app.htm').then(function (data) {
                res.send(data);
            });
        });
        app.use('/', express.static(options.basePath + 'Build'));
        app.use('/Images/', express.static(options.basePath + 'Images'));
        app.use('/Build/', express.static(options.basePath + 'Build'));
        app.use('/Dependencies/', express.static(options.basePath + 'Dependencies'));


        // turn this into a service
        app.get('/Data/:partition/:row', function (req, res) {
            store.get(req.params.partition, req.params.row)
                .then(function (data) {
                    res.send(data);
                })
                .fail(function () {
                    res.status(404).end();
                });
        });


        server.listen(options.port);
    },
    registerService: function (uri, handler, verb) {
        app[verb || 'all'](uri, handler);
    },
    server: server
};