var Mocha = require('mocha'),
    overrides = require('./overrides'),
    convert = require('./convert'),
    loader = require('./loader'),
    runner = require('./runner'),

    mocha = new Mocha();

mocha.ui('tdd');

module.exports = {
    run: function () {
        runner.run(mocha.suite);
    },
    loadFile: function (path, debugPath) {
        loader.loadFile(mocha, path, debugPath);
    },
    loadDirectory: function (path) {
        loader.loadDirectory(mocha, path);
    },
    removeFile: function (path) {
        loader.removeFile(mocha, path);
    },
    tests: function () {
        return convert.suite(mocha.suite);
    }
};

