var build = require('tribe/build'),
    templates = build.templates,
    path = require('path'),
    context;

suite('tribe.build.blocks', function () {
    setup(function () {
        context = {};
    });

    // expecting test3.js to be the first element in the array may be flaky
    test("addFilesToContext", function () {
        return getBlock('addFilesToContext')('test', targetPath('simple'))(context)
            .then(function () {
                expect(context.test.length).to.equal(3);
                expect(context.test[0].path).to.have.string('test3.js');
                expect(context.test[0].content).to.equal('//3');
            });
    });

    test("addPathsToContext", function () {
        return getBlock('addPathsToContext')('test', targetPath('simple'))(context)
            .then(function () {
                expect(context.test.length).to.equal(3);
                expect(context.test[0]).to.have.string('test3.js');
            });
    });

    test("browserify", function () {
        return getBlock('addPathsToContext')('test', targetPath('browserify'))(context)
            .then(function () {
                return getBlock('browserify')('test').to()(context);
            })
            .then(function (result) {
                expect(result).to.have.string('//2');
                expect(result).to.have.string('T.scriptEnvironment =');
            });
    });

    test("combineContents", function () {
        context.test = [{ content: '1' }, { content: '2' }, { content: '3' }];
        var result = getBlock('combineContents')('test').to()(context);
        expect(result).to.equal('1\n2\n3');
    });

    test("renderTemplate", function () {
        templates.add('test', '-<%=context.test%>-');
        context.test = 'value';
        var result = getBlock('renderTemplate')('test').to()(context);
        expect(result).to.equal('-value-');
    });

    function getBlock(name) {
        return require('tribe/build/blocks/' + name);
    }

    function targetPath(name) {
        return path.resolve(__dirname, '../../files/blocks/', name);
    }
});