var steps = require('tribe/build/steps');

suite('tribe.build.steps', function () {
    test('registered steps can be created', function () {
        var stub = sinon.stub().returns('step');
        steps.register('test', stub);
        var step = steps.create({ name: 'test', options: 'options' });

        expect(step).to.equal('step');
        expect(stub.calledOnce).to.be.true;
        expect(stub.firstCall.args[0]).to.equal('options');
    });
});