var Module = require('module');

module.exports = {
    // this is intended to be used in a child process. It should not be used repeatedly.
    redirect: function (from, to) {
        var original = Module._load;
        Module._load = function (request, parent, isMain) {
            if (request === from)
                return original(to, parent, isMain);
            return original(request, parent, isMain);
        };
    }
};