var options = require('tribe/options')
    path = require('path');

module.exports = {
    resolveAppPath: function (targetPath) {
        return path.resolve(options.basePath, targetPath);
    },
    appPathFor: function (fullPath) {
        return fullPath.replace(options.basePath, '').replace(/\\/g, '/');
    },
    resourcePathFor: function (fullPath) {
        return module.exports.appPathFor(fullPath.substring(0, fullPath.lastIndexOf('.')));
    },
    markupIdentifierFor: function (fullPath) {
        return module.exports.resourcePathFor(fullPath).replace(/\//g, '-').replace(/\./g, '');
    }
};