require('tribe').register.actor(function (actor) {
    var self = this,
        state = actor.requires('state');

    actor.handles = {
        'input': function () {
            self.beforePublish(state.count());
            actor.pubsub.publish('logic');
            self.afterPublish(state.count());
        }
    };

    this.beforePublish = ko.observable(0);
    this.afterPublish = ko.observable(0);
});