T.Types.StringCollection = function (initialContent) {
    var content = initialContent ? initialContent.concat() : [];

    content.insertBefore = function (before, item) {
        content.splice(content.indexOf(before), 0, item);
        return content;
    };

    content.insertAfter = function (after, item) {
        content.splice(content.indexOf(after) + 1, 0, item);
        return content;
    };

    return content;
};
