(function () {
    module('Unit.Types.StringCollection');

    test("insertBefore inserts item before matched item", function () {
        var c = T.Types.StringCollection(['a', 'b']);
        deepEqual(c.insertBefore('a', 'a1'), ['a1', 'a', 'b']);
        deepEqual(c.insertBefore('b', 'b1'), ['a1', 'a', 'b1', 'b']);
    });

    test("insertAfter inserts item before matched item", function () {
        var c = T.Types.StringCollection(['a', 'b']);
        deepEqual(c.insertAfter('a', 'a1'), ['a', 'a1', 'b']);
        deepEqual(c.insertAfter('b', 'b1'), ['a', 'a1', 'b', 'b1']);
    });
})();
