suite('tribe.expressions.predicates', function () {
    require('../predicates');

    test("skip", function () {
        expect([1, 2, 3].skip(0)).to.deep.equal([1, 2, 3])
        expect([1, 2, 3].skip(1)).to.deep.equal([2, 3])
        expect([1, 2, 3].skip(2)).to.deep.equal([3])
        expect([1, 2, 3].skip(3)).to.deep.equal([])
    })

    test("take", function () {
        expect([1, 2, 3].take(0)).to.deep.equal([])
        expect([1, 2, 3].take(1)).to.deep.equal([1])
        expect([1, 2, 3].take(2)).to.deep.equal([1, 2])
        expect([1, 2, 3].take(3)).to.deep.equal([1, 2, 3])
    })
})
