var sizeFor = require('./size')
    
module.exports = function(pane) {
    var size = sizeFor(pane.element),
        margins = {
            top: 20,
            right: 10,
            bottom: 20,
            left: 20
        }

    return {
        width: (pane.data.width || size.width()) - margins.left - margins.right,
        height: (pane.data.height || size.height() / 2) - margins.top - margins.bottom,
        margins: margins,
        colors: pane.data.colors || ['steelblue', 'sandybrown', 'cadetblue', 'chocolate', 'darkcyan', 'darkseagreen', 'goldenrod'],
        type: pane.data.type || 'incremental'
    }
}