var Rx = require('rx')

Rx.Observable.prototype.topic = function (topic) {
    return this.where(function (x) { return x && x.topic === topic })
}

Rx.Observable.prototype.data = function (property) {
    return this.select(function (x) {
        return property
            ? x && x.data && x.data[property]
            : x && x.data;
    })
}
