var Rx = require('rx'),
    register = require('tribe/register')

register.vocabulary = vocabulary;
module.exports = {
    register: vocabulary
}

function vocabulary(terms) {
    Object.keys(terms).forEach(function (term) {
        var expression = terms[term];
        Rx.Observable.prototype[term] = function () {
            return expression.apply(this, [this].concat(Array.prototype.slice.apply(arguments)))
        }
    })
}
