var actors = require('tribe/actors'),
    _ = require('underscore');

module.exports = {
    apply: function () {
        // this is a hack and flaky - relies on a specific property chain being set
        // we essentially need to map the pubsub/filters collection here
        // the only messageFilter we have defined is a property of the data object, assume this is set
        // this whole thing is likely to change... Rx would be nice!
        var paths = _.reduce(actors.actors, function (result, actor) {
            if (actor.definition.messageFilter)
                result.push('data.' + actor.definition.messageFilter.options.property);
        }, []);


    }
}