var actors = require('tribe/actors');

module.exports = function (options) {
    var actor = actors.actors[options.name],
        eventStore = require('tribe/storage').entity('messages');
    if(actor && actor.definition.messageFilter) {
        return eventStore.retrieve({
            p: 'data.' + actor.definition.messageFilter.options.property,
            v: options.id
        }).then(function (envelopes) {
            return { envelopes: envelopes };
        });
    }
};