Array.prototype.select = function (property) {
    return this.map(function (item) {
        item = ko.unwrap(item);
        return item ? item[property] : item;
    });
};

Array.prototype.selectProperties = function () {
    var properties = Array.from(arguments);

    return this.reduce(function (result, item) {
        item = ko.unwrap(item);
        properties.forEach(function (property) {
            if(!result[property]) result[property] = []; 
            result[property].push(item ? item[property] : undefined);
        });    
        return result;
    }, {});
};