var actor = require('tribe.pubsub/actor'),
    _startChild = actor.prototype.startChild;

actor.prototype.startChild = function (child, onstartData) {
    if (child.constructor === String)
        child = actorDefinition(child);

    _startChild.call(this, child, onstartData);
}

function actorDefinition(path) {
    if (path.charAt(0) !== '/')
        path = '/' + path;

    return T.context().actors[path].constructor;
}
