var register = require('tribe/register'),
    markupIdentifierFor = require('tribe/utilities/paths').markupIdentifierFor;

module.exports = {
    add: function(path, pane) {
        if(pane.template) {
            var element = '<script type="text/template" id="' + markupIdentifierFor('/__' + path, 'template') + '">' + pane.template + '</script>';
            $('head').append(element);
        }

        if(pane.style)
            $('head').append('<style>' + pane.style + '</style>');

        if(pane.model)
            register.model(pane.model, '/__' + path)
    }
}
