var register = require('tribe/register'),
    markupIdentifierFor = require('tribe/utilities/paths').markupIdentifierFor;

module.exports = {
    add: function(path, template, model) {
        var element = '<script type="text/template" id="' + markupIdentifierFor('/__' + path, 'template') + '">' + template + '</script>';
        $('head').append(element);

        if(model)
            register.model(model, '/__' + path)
    }
}
