var options = require('tribe/options'),
    eventStore = require('tribe/client/eventStore');

$(document).keypress(function (e) {
    if (e.which === 28) // ctrl-\
        window.open('http://' + window.location.hostname + ':' + options.inspectorPort + '/debug?port=' + options.debugPort, 'debugger');
    else if (e.which === 29) // ctrl-]
        eventStore.clear();
});
