var log = require('tribe/logger')
    serializer = require('tribe/utilities/serializer'),

    api = module.exports = {
        put: function (partitionKey, rowKey, data) {
            localStorage.setItem(partitionKey + '-' + rowKey, serializer.serialize(data));
        },
        get: function (partitionKey, rowKey) {
            return serializer.deserialize(localStorage.getItem(partitionKey + '-' + rowKey));
        }
    };