suite('tribe.browser.client.Array.extensions', function () {
    suite('selectProperties', function () {
        test('projects properties from objects in an array', function () {
            var source = [
                { p1: 1, p2: '1' },
                { p1: 2, p2: '2' }
            ];
            expect(source.selectProperties('p1', 'p2')).to.deep.equal({
                p1: [1, 2],
                p2: ['1', '2']
            });
        })
    });
});