suite('tribe.browser.client.builtins', function () {
    test("flow template was rendered", function () {
        expect($('#template--__flow').html()).to.contain('<!--');
    });

    test("flow pane can be rendered", function () {
        T.createNode('body', { path: '/__flow' });
        expect($('body').html()).to.contain('<!--');
    });

    test("overlay pane can be rendered", function () {
        T.createNode('body', { path: '/__overlay', data: { pane: 'tests/overlay', transition: 'none' } });
        expect($('.__overlay').html()).to.contain('overlayContent');
    });

    test("overlay pane takes path from parent pane", function () {
        T.createNode('body', { path: 'tests/overlayHost' });
        expect($('.__overlay').html()).to.contain('overlayContent');
    });
});
