suite('tribe.browser.integration.bindingHandlers.field', function () {
    test("fields are added to pane model on render", function () {
        var node = T.createNode('body', { path: '/tests/field' })
        expect(node.pane.model.fields.test()).to.equal('');
    })

    test("fields are updated on value change", function () {
        var node = T.createNode('body', { path: '/tests/field' })
        $('input').val('value').change()
        expect(node.pane.model.fields.test()).to.equal('value')
    })

    test("field value is set from element value", function () {
        var node = T.createNode('body', { path: '/tests/field' })
        expect(node.pane.model.fields.elementValue()).to.equal('element');
    })

    test("field value is set from value binding", function () {
        var node = T.createNode('body', { path: '/tests/field' })
        expect(node.pane.model.fields.bindingValue()).to.equal('binding');
    })
})
