suite('tribe.browser.integration.field', function () {
    test("fields are added to pane model on render", function () {
        var node = T.createNode('body', { path: '/tests/field' })
        expect(node.pane.model.fields.test()).to.equal('');
    })

    test("fields are updated on value change", function () {
        var node = T.createNode('body', { path: '/tests/field' })
        $('input').val('value').change()
        expect(node.pane.model.fields.test()).to.equal('value')
    })
})
