var pubsub = require('tribe.pubsub'),
    uuid = require('node-uuid'),
    Q = require('q')

suite('tribe.browser.integration.rx', function () {
    test("messages property is added to model", function () {
        var id = uuid.v4(),
            node = T.createNode('body', { path: '/tests/rx', scope: { actorId: id } })
        return Q(node.pane.is.rendered).then(function () {
            pubsub.publishSync('topic', { actorId: id, value: 'aa' })
            pubsub.publishSync('topic', { actorId: id, value: 'ab' })
            pubsub.publishSync('topic', { actorId: id, value: 'ba' })
            expect($('li').length).to.equal(2)
        })
    });
});
