suite('tribe.client.flow', function () {
    var flowModule = require('tribe/client/flow'),
        pubsubModule = require('tribe.pubsub/pubsub'),
        actorModule = require('tribe.pubsub/actor'),
        pubsub

    setup(function () {
        pubsub = new pubsubModule({ sync: true })
    })

    test("flow sets start pane", function () {
        var actor = new actorModule(pubsub, flowModule(function (flow) {
            flow.startsAt('test')
        }))
        expect(actor.instance.pane()).to.equal('test')
    })

    test("flow sets pane when to is called", function () {
        var to,
            actor = new actorModule(pubsub, flowModule(function (flow) {
                to = flow.to
            }))
        to('test')('data')
        expect(actor.instance.pane()).to.deep.equal({ path: 'test', data: 'data' })
    })

    test("flow attaches handlers", function () {
        var actor = new actorModule(pubsub, flowModule(function (flow) {
            flow.on('topic').to('test')
        }))
        actor.start()
        pubsub.publish('topic', 'data')
        expect(actor.instance.pane()).to.deep.equal({ path: 'test', data: 'data' })
    })

    test("flow attaches rx expressions", function () {
        var actor = new actorModule(pubsub, flowModule(function (flow) {
            flow.messages.count().when(2).then(flow.to('test'))
        }))
        actor.start()
        pubsub.publish('topic')
        expect(actor.instance.pane()).to.be.undefined
        pubsub.publish('topic')
        expect(actor.instance.pane()).to.deep.equal({ path: 'test', data: 2 })
    })
})
