suite('tribe.utilities.collections', function () {
    var collections = require('tribe/utilities/collections');

    test("sortObject", function () {
        expect(collections.sortObject({ a: 1, c: 3, b: 2 })).to.deep.equal({ a: 1, b: 2, c: 3 });
    });

    test("pluckArrays with single property", function () {
        expect(collections.pluckArrays({ a1: [1], a2: [2, 3] }, 'a2')).to.deep.equal([2, 3]);
    })

    test("pluckArrays with multiple properties", function () {
        expect(collections.pluckArrays({ a1: [1], a2: [2, 3] }, ['a1', 'a2'])).to.deep.equal([1, 2, 3]);
    })

    test("pluckArrays ignores missing properties", function () {
        expect(collections.pluckArrays({ a2: [2, 3] }, ['a1', 'a2'])).to.deep.equal([2, 3]);

    })
});
