var _ = require('underscore');

module.exports = {
    sortObject: function (map) {
        var keys = _.sortBy(_.keys(map), function (a) { return a; });
        var newmap = {};
        _.each(keys, function (k) {
            newmap[k] = map[k];
        });
        return newmap;
    },
    pluckArrays: function (target, properties) {
        return (properties.constructor === Array ? properties : [properties]).reduce(function (results, property) {
            return target[property] ? results.concat(target[property]) : results;
        }, []);
    }
}
